where.if:
	
		    and a.po_no in (
		    	SELECT 
					distinct a1.po_no
    			FROM doc_po_header a1
    			LEFT JOIN doc_po_details d ON a1.po_no = d.po_no
    			WHERE d.sku = #{sku,jdbcType=VARCHAR}					   			 		
				
					and a1.from_shop_id = #{fromShopId}
				  		
				
					and a1.to_shop_id = #{toShopId}
				  		
				
					and a1.po_status = #{poStatus}
				  		
				
					and a1.po_type = #{poType,jdbcType=VARCHAR}
								
		    )
